  --
  -- f_queryRemoveOrderBy
  --
  function f_queryRemoveOrderBy(
    pi_sql_query in varchar2
  ) return varchar2 is
  begin
    return REGEXP_REPLACE(pi_sql_query, '(\s{0,})order(\s{1,})by(\s{1,})([^\sdecode])(.*)|(\s{1,})(order(\s{1,})by(\s{1,})decode\([^\)]*\))', '');
  end f_queryRemoveOrderBy;

