   RETURN VARCHAR2
IS
   FIRST_TOKEN   VARCHAR2 (2000);
   COMA_POS      NUMBER;
   CONTENIDO     VARCHAR2 (2000) := LINEBUF;
   i             NUMBER := 0;
BEGIN
   IF CONTENIDO IS NULL
   THEN
      FIRST_TOKEN := '';
   ELSE
      LOOP
         EXIT WHEN i = col;
         COMA_POS := INSTR (CONTENIDO, separa);

         IF COMA_POS = 0
         THEN
            FIRST_TOKEN := CONTENIDO;
            CONTENIDO := NULL;
         ELSE
            IF COMA_POS = 1
